package components

import (
	"encoding/base64"
	"image"
	_ "image/gif"
	_ "image/jpeg"
	_ "image/png"
	"log"
	"strings"

	"github.com/gizak/termui/v3/widgets"
)

type ImageComponent struct {
	Root *widgets.Image
}

func InitImageComponent() *ImageComponent {
	//const GOPHER_IMAGE = ``
	const GOPHER_IMAGE = ``
	img, _, err := image.Decode(base64.NewDecoder(base64.StdEncoding, strings.NewReader(GOPHER_IMAGE)))
	if err != nil {
		log.Fatalf("failed to decode gopher image: %v", err)
	}
	image := widgets.NewImage(img)
	return &ImageComponent{Root: image}
}
